           this.map = new Sprite();

           this.floorTile = new Sprite();
           this.wallTile = new Sprite();

           this.floorImg = new Image();
           this.wallImg = new Image();
           this.floorImg.src = "img/FloorTile.png";
           this.wallImg.src = "img/wall.png";
          
            this.pos = 0;
          
             
            

           this.mapImg = new Image();   // Create new img element
